class Solution:
    # @param A : list of integers
    # @return an integer
    def maxArr(self, A):
        
        s = float('-inf')
        
        def solve(A):

            if len(A) == 0:
                return float('-inf')

            if len(A) == 1:
                return 0

            mx_right = 0
            mx_left = 0
            mn_right = 0
            mn_left = 0

            for i in range(1, len(A)):
                if A[i][1] >= A[mx_right][1]:
                    mx_right = i

                if A[i][1] > A[mx_left][1]:
                    mx_left = i
                
                if A[i][1] <= A[mn_right][1]:
                    mn_right = i

                if A[i][1] < A[mn_left][1]:
                    mn_left = i

            mx = 0
            mn = 0
            
            if abs(A[mx_left][0] - A[mn_right][0]) >= abs(A[mx_right][0] - A[mn_left][0]):
                mx = max(mx_left, mn_right)
                mn = min(mx_left, mn_right)
            else:
                mx = max(mx_right, mn_left)
                mn = min(mx_right, mn_left)

            s = int(abs(A[mx][1] - A[mn][1]) + abs(A[mx][0] - A[mn][0]))
            
            sliced_1 = []
            sliced_2 = []

            if len(A) > 2:
                if mn < len(A):
                    sliced_1 = A[0:mn + 1]
                    sliced_2 = A[0:mn]
                    
                if mx < len(A):
                    sliced_2 = sliced_2 + A[mx:]

                if mx + 1 < len(A):
                    sliced_1 = sliced_1 + A[mx+1:]

            if len(sliced_1) > 0:
                s = max(s, solve(sliced_1))
            
            if len(sliced_2) > 0:
                s = max(s, solve(sliced_2))

            return s

        return solve(list(enumerate(A)))

